import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TextInput, Button, Pressable, Keyboard, Alert, TouchableOpacity } from 'react-native';
import * as firebase from 'firebase';
import ExpoStatusBar from 'expo-status-bar/build/ExpoStatusBar';
import * as Facebook from 'expo-facebook';
import Colors from '../constant/Colors';

const LoginScreen = props => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [currentUserId, setCurrentUserId] = useState('');

    useEffect(() => {
        Facebook.initializeAsync({ appId: '1328577777474619', appName: 'EmployeeManagment' });

        firebase.auth().onAuthStateChanged((user => {
            // console.log("on change User", user);
            if (user != null) {
                props.navigation.navigate('ManagementEmployeeScreen');
            }
        }));
    }, [])

    const SignupUser = async (email, password) => {
        await firebase.auth().createUserWithEmailAndPassword(email, password)
            .then((user) => {
                console.log("SignupUser", user);
                setCurrentUserId(user.uid);
            })
            .catch((error) => {
                var errorCode = error.code;
                var errorMessage = error.message;
                throw error;
            });
    }

    const signupHandler = async () => {
        try {
                // const res = await fetch("/api/signup");
                // const data = await res.json();
                // console.log(data);
            // setCurrentUserId(user.uid);

            //Signup
            await SignupUser(email, password);
        }
        catch (error) {
            signoutUser();
            return Alert.alert(
                'שגיאת התחברות',
                error.message,
                [
                    { text: "אישור" }
                ],
                { cancelable: true }
            );
        }
    }

    const SigninUser = async (email, password) => {
        await firebase.auth().signInWithEmailAndPassword(email, password)
            .then((res) => {
                console.log("Signin succuss");
                let userUid = res.user.uid;
                setCurrentUserId(userUid);

            })
            .catch((error) => {
                var errorCode = error.code;
                var errorMessage = error.message;
                // setError(errorMessage);
                throw error;
            });
    }

    const loginHandler = async () => {
        try {
            await SigninUser(email, password);

        } catch (error) {
            signoutUser();
            return Alert.alert(
                'שגיאת התחברות',
                error.message,
                [
                    { text: "אישור" }
                ],
                { cancelable: true }
            );
        }
    }

    const facebookLoginHandler = async () => {

        const { type, token } = await
            Facebook.logInWithReadPermissionsAsync(
                {
                    permission: "public_profile"
                }
            );

        if (type == 'succuss') {
            const credential = new firebase.auth.FacebookAuthProvider().credential(token);

            await firebase.auth().signInWithCredential(credential).catch((error) => {
                console.log("error", error);
            })
            // firebase
            //     .auth()
            //     .signInWithPopup(provider)
            //     .then((result) => {
            //         var credential = result.credential;

            //         // The signed-in user info.
            //         var user = result.user;

            //         // This gives you a Facebook Access Token. You can use it to access the Facebook API.
            //         var accessToken = credential.accessToken;

            //         // ...
            //         console.log("facebookLoginHandler succuss", user);
            //     })
            //     .catch((error) => {
            //         // Handle Errors here.
            //         var errorCode = error.code;
            //         var errorMessage = error.message;
            //         // The email of the user's account used.
            //         var email = error.email;
            //         // The firebase.auth.AuthCredential type that was used.
            //         var credential = error.credential;
            //         console.log("errorMessage", errorMessage);
            //         // ...
            //     });
        }
    }

    const signoutUser = async () => {
        firebase.auth().signOut().then(() => {
            console.log("signout succuss");
            // Sign-out successful.
        }).catch((error) => {
            // An error happened.
            console.log("signout fail");
        });
    }

    return (
        <Pressable style={styles.root} onPress={() => Keyboard.dismiss()}>
            <View style={styles.inputContainer}>
                <Text style={styles.title}>Login/Sign Up</Text>
                <TextInput placeholder='Email' onChangeText={text => setEmail(text)} keyboardType="email-address" style={styles.inputText} autoCapitalize='none' ></TextInput>
                <TextInput placeholder='Password' onChangeText={text => setPassword(text)} secureTextEntry={true} style={styles.inputText} autoCapitalize='none'></TextInput>

                <TouchableOpacity onPress={loginHandler} style={styles.BtnLogin} >
                    <Text>Login</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={signupHandler} style={{ ...styles.BtnLogin, backgroundColor: '#e9ecef' }}>
                    <Text>Sign up</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={facebookLoginHandler} style={{ ...styles.BtnLogin, backgroundColor: '#3b5998', }} >
                    <Text style={{ color: 'white' }}>Sign in with Facebook</Text>
                </TouchableOpacity>

            </View>
        </Pressable>
    )
}

const styles = StyleSheet.create({
    root: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: Colors.primary
    },
    inputContainer: {
        width: '80%',
        height: '60%',
        alignContent: 'center',
        alignSelf: 'center',
        justifyContent: 'center',
        alignItems: 'center',
        borderWidth: 2,
        borderRadius: 10,
        borderColor: Colors.lightGray,
        backgroundColor: Colors.lightGray

    },
    title: {
        // height: 100,
        fontSize: 20,
        padding: 4,
        marginBottom: 30,
        fontWeight:'bold',
    },
    inputText: {
        width: '85%',
        height: 40,
        backgroundColor: Colors.white,
        borderRadius: 20,
        margin: 5,
        textAlign: 'center'
    },
    BtnLogin: {
        width: '60%',
        padding: 8,
        borderRadius: 8,
        borderWidth: 1,
        borderColor: Colors.lightGray,
        alignItems:'center',
        // backgroundColor: '#e8eddf',
        backgroundColor: Colors.offright,
        margin: 3,
    },
    
});

export default LoginScreen;
